using System; namespace Project3 {     public enum Conduct { good, poor, excellent }      internal class student     {         internal string Name { get; set; }         internal string LastName { get; set; }         internal int Age { get; set; }         internal string Phone { get; set; }         internal double Height { get; set; }         internal double Tuition { get; set; }         internal DateTime Date { get; set; }         //static mporeis na kaleseis thn methodo 'h to enum apo opoiodipote shmeio tou kwdika!         static Conduct Conduct { get; set; }          public student(string name, string lastname, int age, double height, double tuition, DateTime date, string phone,Conduct conduct)         {             Name = name;             LastName = lastname;             Age = age;             Phone = phone;             Height = height;             Tuition = tuition;             Date = date;             Conduct = conduct;         }         public override string ToString()
        {
            return ($"{Name} | {LastName} | {Age} | {Height} | {Tuition} | {Date.ToShortDateString()} |{Phone} | {Conduct}");
        }      } } 